import colorama
colorama.init()

def stinRun(message,func):
    print(message + " ... ",end='')
    data = None
    try:
        data = func()
    except Exception as e:
        print(u"\u001b[31mFAIL\u001b[0m")
    else:
        if data:
            return data
        print(u"\u001b[32mDONE\u001b[0m")